import React, { useState } from 'react';
// import 'HomePage.css';

const HomePage = () => {
    // Створюємо стан для збереження обраних інгредієнтів та ціни піци
    const [selectedIngredients, setSelectedIngredients] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);

    // Функція для додавання обраного інгредієнта
    const addIngredient = (ingredient) => {
        // Перевіряємо, чи інгредієнт вже був обраний
        const isIngredientSelected = selectedIngredients.some(
            (selected) => selected.id === ingredient.id
        );

        if (!isIngredientSelected) {
            // Додаємо новий обраний інгредієнт до стану
            setSelectedIngredients([...selectedIngredients, ingredient]);
            // Оновлюємо загальну ціну піци
            setTotalPrice(totalPrice + ingredient.price);
        }
    };

    // Функція для видалення обраного інгредієнта
    const removeIngredient = (ingredient) => {
        // Фільтруємо обрані інгредієнти і видаляємо вибраний
        const updatedIngredients = selectedIngredients.filter(
            (selected) => selected.id !== ingredient.id
        );

        // Оновлюємо стан і загальну ціну піци
        setSelectedIngredients(updatedIngredients);
        setTotalPrice(totalPrice - ingredient.price);
    };

    return (
        <div>
            <h2>Конструктор піци</h2>
            <div>
                {/* Відображення доступних інгредієнтів */}
                <h3>Доступні інгредієнти:</h3>
                {/* Описати список доступних інгредієнтів */}
            </div>
            <div>
                {/* Відображення обраних інгредієнтів */}
                <h3>Обрані інгредієнти:</h3>
                {/* Описати список обраних інгредієнтів */}
            </div>
            <div>
                {/* Відображення загальної ціни піци */}
                <h3>Загальна ціна: {totalPrice}</h3>
            </div>
        </div>
    );
};

export default HomePage;
